import 'package:flutter/material.dart';

// used the same urls from a friend

const String winterImgUrl =
    "https://www.shutterstock.com/shutterstock/photos/1480338545/display_1500/stock-vector-hello-winter-banner-cards-cartoon-pop-vintage-happy-winter-card-snowman-1480338545.jpg";
const String summerImgUrl =
    "data:image/jpeg;base64,";
const String autumnImgUrl =
    "data:image/jpeg;base64,";
const String springImgUrl =
    "data:image/jpeg;base64,";

void main() {
  runApp(const MaterialApp(debugShowCheckedModeBanner: false, home: MyApp()));
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Padding(
        padding: EdgeInsets.all(8.0),
        child: Row(
          mainAxisAlignment: MainAxisAlignment.spaceEvenly,
          children: [
            SeasonCard(currentSeason: "Spring", city: "Tokyo"),
            SizedBox(width: 10),
            SeasonCard(currentSeason: "Summer", city: "Phnom Penh"),
          ],
        ),
      ),
    );
  }
}

class SeasonCard extends StatefulWidget {
  final String currentSeason;
  final String city;
  const SeasonCard({
    super.key,
    required this.currentSeason,
    required this.city,
  });

  @override
  State<SeasonCard> createState() => _SeasonCardState();
}

class _SeasonCardState extends State<SeasonCard> {
  Map<String, Image> images = {
    "Spring": Image.network(
      springImgUrl,
      fit: BoxFit.cover,
      height: 500,
      width: 400,
    ),
    "Summer": Image.network(
      summerImgUrl,
      fit: BoxFit.cover,
      height: 500,
      width: 400,
    ),
    "Autumn": Image.network(
      autumnImgUrl,
      fit: BoxFit.cover,
      height: 500,
      width: 400,
    ),
    "Winter": Image.network(
      winterImgUrl,
      fit: BoxFit.cover,
      height: 500,
      width: 400,
    ),
  };

  late String curSeason = widget.currentSeason;
  
  void changeSeason() {
    setState(() {
      switch (curSeason) {
        case "Spring":
          curSeason = "Summer";
          break;
        case "Summer":
          curSeason = "Autumn";
          break;
        case "Autumn":
          curSeason = "Winter";
          break;
        case "Winter":
          curSeason = "Spring";
          break;
      }
    });
  }

  @override
  Widget build(BuildContext context) {
    return Container(
      height: 500,
      width: 350,
      decoration: BoxDecoration(
        border: Border.all(color: Colors.black, width: 2),
      ),
      child: Column(
        children: [
          TextButton(
            onPressed: changeSeason,
            style: ButtonStyle(
              padding: WidgetStateProperty.all(EdgeInsets.zero),
              minimumSize: WidgetStateProperty.all(Size.zero),
              tapTargetSize: MaterialTapTargetSize.shrinkWrap,
              overlayColor: WidgetStateProperty.all(Colors.transparent),
              backgroundColor: WidgetStateProperty.all(Colors.transparent),
            ),
            child: ClipRRect(child: images[curSeason]!),
          ),
          Expanded(
            child: Container(
              decoration: BoxDecoration(
                border: Border(top: BorderSide(color: Colors.black, width: 2)),
              ),
              child: Center(child: Text(widget.city)),
            ),
          ),
        ],
      ),
    );
  }
}
